import React,{ useState, useEffect } from "react";
import "./Footer.scss";
import { useStateValue } from "../../store/state";
import jagotalogo from "../../assets/images/jagota_logo.png";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import LazyLoad from 'react-lazyload';
import axios from "axios";
import request from "../../request";
import { toast } from "react-toastify";

function Footer({maintenance}) {
  const { t } = useTranslation();
  const [{}, dispatch] = useStateValue();
  const closeSide = () => {
    dispatch({
      type: "SET_MINICART",
      value: false,
    });
    dispatch({
      type: "SET_MINIMENU",
      value: false,
    });
    dispatch({
      type: "SET_NOTIFICATION",
      value: false,
    });
    dispatch({
      type: "SET_LANGUAGE",
    });
    dispatch({
      type: "SEARCHBAR__CLOSEOPEN",
    });
  };
  const [cookopen, setCookOpen] = useState(false);

  const cancelStore = () => {
    localStorage.setItem('cookiespop', false)
    setCookOpen(false);
  }

  useEffect(async() => {
    if (localStorage.getItem("userdata") !== null) {
      try {
        const getTerms = await axios({
          method: 'get',
          url: `${request?.getTerms}${localStorage.getItem('userid')}`
        })

        console.log(getTerms?.data[0]?.termsConditionStatus)
        if(getTerms?.data[0]?.termsConditionStatus == "0") {
          localStorage.clear();
        }
      } catch(e) {
        console.log(e)
      }
    }
  }, []);

  const onCookOpenModal = () => setCookOpen(true);
  const onCookCloseModal = async() => {
    try {await axios({
        method: "post",
        url: `${request?.setcookies}${localStorage.getItem('userid')}`
      })
      setCookOpen(false);
    } catch(e) {
      console.log(e);
      setCookOpen(false);
    }
  }

  useEffect(async() => {

    if(JSON.parse(localStorage.getItem('cookiespop')) == false) {
      return
    }
    try {
      let cookiedata = await axios({
        method: 'get',
        url: `${request?.getcookies}${localStorage.getItem('userid')}`
      })
      if(cookiedata.data[0]?.cookieStatus == "0") {
        localStorage.setItem('cookiespop', true)
        onCookOpenModal()
      } else {
        localStorage.setItem('cookiespop', false)
        setCookOpen(false);
      }
    } catch(e) {
      console.log(e)
    }
  }, [])

  return (
    <div className="footer" onClick={closeSide}>
      {cookopen &&
        <div open={cookopen} className="customModal__cookies" center>
          <h2>{t("We value your privacy")}</h2>
          <p>
            <span>{t("We use cookies to ensure that we give you the best experience on our website.")} </span>
            <span>{t("We also use cookies to ensure we show you the advertising that is relevant to you. For more information please visit")} <Link to="/cookies">
            {t("Cookie Policy")}
            </Link></span>
          </p>
          <p className="cookies__button">
            <span onClick={onCookCloseModal}>{t("Accept")}</span>
            <span onClick={() => cancelStore()}>{t("Cancel")}</span>
          </p>
        </div>
      }
      <div className="footer__main">
        <div className="footer__col1">
          <LazyLoad height={20}>
            <img src={jagotalogo} alt="" />
            </LazyLoad>  
          <p>{t("Finest Ingredients From Around The World")}</p>
          <div className="footer__icons">
            <LazyLoad height={16}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-facebook"
                viewBox="0 0 16 16"
              >
                <path d="M16 8.049c0-4.446-3.582-8.05-8-8.05C3.58 0-.002 3.603-.002 8.05c0 4.017 2.926 7.347 6.75 7.951v-5.625h-2.03V8.05H6.75V6.275c0-2.017 1.195-3.131 3.022-3.131.876 0 1.791.157 1.791.157v1.98h-1.009c-.993 0-1.303.621-1.303 1.258v1.51h2.218l-.354 2.326H9.25V16c3.824-.604 6.75-3.934 6.75-7.951z" />
              </svg>
            </LazyLoad>
            <LazyLoad height={16}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-instagram"
                viewBox="0 0 16 16"
              >
                <path d="M8 0C5.829 0 5.556.01 4.703.048 3.85.088 3.269.222 2.76.42a3.917 3.917 0 0 0-1.417.923A3.927 3.927 0 0 0 .42 2.76C.222 3.268.087 3.85.048 4.7.01 5.555 0 5.827 0 8.001c0 2.172.01 2.444.048 3.297.04.852.174 1.433.372 1.942.205.526.478.972.923 1.417.444.445.89.719 1.416.923.51.198 1.09.333 1.942.372C5.555 15.99 5.827 16 8 16s2.444-.01 3.298-.048c.851-.04 1.434-.174 1.943-.372a3.916 3.916 0 0 0 1.416-.923c.445-.445.718-.891.923-1.417.197-.509.332-1.09.372-1.942C15.99 10.445 16 10.173 16 8s-.01-2.445-.048-3.299c-.04-.851-.175-1.433-.372-1.941a3.926 3.926 0 0 0-.923-1.417A3.911 3.911 0 0 0 13.24.42c-.51-.198-1.092-.333-1.943-.372C10.443.01 10.172 0 7.998 0h.003zm-.717 1.442h.718c2.136 0 2.389.007 3.232.046.78.035 1.204.166 1.486.275.373.145.64.319.92.599.28.28.453.546.598.92.11.281.24.705.275 1.485.039.843.047 1.096.047 3.231s-.008 2.389-.047 3.232c-.035.78-.166 1.203-.275 1.485a2.47 2.47 0 0 1-.599.919c-.28.28-.546.453-.92.598-.28.11-.704.24-1.485.276-.843.038-1.096.047-3.232.047s-2.39-.009-3.233-.047c-.78-.036-1.203-.166-1.485-.276a2.478 2.478 0 0 1-.92-.598 2.48 2.48 0 0 1-.6-.92c-.109-.281-.24-.705-.275-1.485-.038-.843-.046-1.096-.046-3.233 0-2.136.008-2.388.046-3.231.036-.78.166-1.204.276-1.486.145-.373.319-.64.599-.92.28-.28.546-.453.92-.598.282-.11.705-.24 1.485-.276.738-.034 1.024-.044 2.515-.045v.002zm4.988 1.328a.96.96 0 1 0 0 1.92.96.96 0 0 0 0-1.92zm-4.27 1.122a4.109 4.109 0 1 0 0 8.217 4.109 4.109 0 0 0 0-8.217zm0 1.441a2.667 2.667 0 1 1 0 5.334 2.667 2.667 0 0 1 0-5.334z" />
              </svg>
            </LazyLoad>
            <LazyLoad height={16}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-twitter"
                viewBox="0 0 16 16"
              >
                <path d="M5.026 15c6.038 0 9.341-5.003 9.341-9.334 0-.14 0-.282-.006-.422A6.685 6.685 0 0 0 16 3.542a6.658 6.658 0 0 1-1.889.518 3.301 3.301 0 0 0 1.447-1.817 6.533 6.533 0 0 1-2.087.793A3.286 3.286 0 0 0 7.875 6.03a9.325 9.325 0 0 1-6.767-3.429 3.289 3.289 0 0 0 1.018 4.382A3.323 3.323 0 0 1 .64 6.575v.045a3.288 3.288 0 0 0 2.632 3.218 3.203 3.203 0 0 1-.865.115 3.23 3.23 0 0 1-.614-.057 3.283 3.283 0 0 0 3.067 2.277A6.588 6.588 0 0 1 .78 13.58a6.32 6.32 0 0 1-.78-.045A9.344 9.344 0 0 0 5.026 15z" />
              </svg>
            </LazyLoad>
          </div>
        </div>
        <div className="footer__col2">
          <h5 className="footer__title">{t("About Us")}</h5>
          <p><Link to="/about">{t("Our Story")}</Link></p>
          <p>{t("Terms and Conditions of Use")}</p>
          <p>{t("Privacy Policy")}</p>
          <p><Link to="/cookies">Cookies</Link></p>
        </div>
        <div className="footer__col3">
          <h5 className="footer__title">{t("Shopping with Us")}</h5>
          <p>{t("How it Works")}</p>
          <p>{t("Why shop with us?")}</p>
          <p>{t("FAQ's")}</p>
          <p>{t("Delivery & Payments")}</p>
          <p><Link to="/return">{t("Return & Refund Policy")}</Link></p>
          <p>{t("Contact Us")}</p>
        </div>
        <div className="footer__col4">
          <h5 className="footer__title">{t("Sell With Us")}</h5>
          <p>{t("Why join?")}</p>
          <p>{t("Apply to sell with us")}</p>
        </div>
      </div>
      <div className="footer__mobile">
        <div className="accordion" id="accordionExample">
          <div className="accordion-item">
            <h2 className="accordion-header" id="headingOne">
              <button
                className="accordion-button collapsed"
                type="button"
                data-toggle="collapse"
                data-target="#collapseOne"
                aria-expanded="true"
                aria-controls="collapseOne"
              >
                {t("About Us")}
              </button>
            </h2>
            <div
              id="collapseOne"
              className="accordion-collapse collapse "
              aria-labelledby="headingOne"
              data-bs-parent="#accordionExample"
            >
              <div className="accordion-body">
                <p>{t("Our Story")}</p>
                <p>{t("Terms and Conditions of Use")}</p>
                <p>{t("Privacy Policy")}</p>
              </div>
            </div>
          </div>
          <div className="accordion-item">
            <h2 className="accordion-header" id="headingTwo">
              <button
                className="accordion-button collapsed"
                type="button"
                data-toggle="collapse"
                data-target="#collapseTwo"
                aria-expanded="true"
                aria-controls="collapseTwo"
              >
                {t("Shopping with Us")}
              </button>
            </h2>
            <div
              id="collapseTwo"
              className="accordion-collapse collapse"
              aria-labelledby="headingTwo"
              data-bs-parent="#accordionExample"
            >
              <div className="accordion-body">
                <p>{t("How it Works")}</p>
                <p>{t("Why shop with us?")}</p>
                <p>{t("FAQ's")}</p>
                <p>{t("Delivery & Payments")}</p>
                <p>{t("Return & Refund Policy")}</p>
                <p>{t("Contact U")}s</p>
              </div>
            </div>
          </div>
          <div className="accordion-item">
            <h2 className="accordion-header" id="headingThree">
              <button
                className="accordion-button collapsed"
                type="button"
                data-toggle="collapse"
                data-target="#collapseThree"
                aria-expanded="true"
                aria-controls="collapseThree"
              >
                {t("Sell With Us")}
              </button>
            </h2>
            <div
              id="collapseThree"
              className="accordion-collapse collapse "
              aria-labelledby="headingThree"
              data-bs-parent="#accordionExample"
            >
              <div className="accordion-body">
                <p>{t("Why join?")}</p>
                <p>{t("Apply to sell with us")}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="footer__copyright">
        <p>
          {t(
            "Copyright 2010 - 2022. Jagota Brothers Trading Co.ltd. All Rights Reserved"
          )}
        </p>
      </div>
    </div>
  );
}

export default Footer;
